package com.tepth.latte.ec.pay;import android.text.TextUtils;import java.util.Map;/** * Description:支付宝支付结果处理工具 * * @author Hequn.Lee * @date 2018/1/22 */public class PayResult {    private String resultStatus;    private String result;    private String memo;    PayResult(String rawResult) {        if (TextUtils.isEmpty(rawResult)) {            return;        }        final String[] resultParams = rawResult.split(";");        for (String resultParam : resultParams) {            if (resultParam.startsWith("resultStatus")) {                resultStatus = getValue(resultParam, "resultStatus");            } else if (resultParam.startsWith("result")) {                result = getValue(resultParam, "result");            } else if (resultParam.startsWith("memo")) {                memo = getValue(resultParam, "memo");            }        }    }    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    private String getValue(String content, String key) {        final String prefix = key + "={";        return content.substring(content.indexOf(prefix) + prefix.length(),                content.lastIndexOf("}"));    }    /**     * @return the resultStatus     */    String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    String getResult() {        return result;    }}